// TODO: Move all hot loaders in here
const { reload } = require("../hot-loader-helper");

module.exports = function (source) {
    const hmrStyleType = 'style';
    // const appCssResource = './app.css';
    // console.log("---> app-style-hot-loader this", this);
    console.log("---> app-style-hot-loader this.context", this.context);
    const resourceFilePath = this.resourcePath.replace(this.context, '.');
    console.log("---> app-style-hot-loader resourceFilePath", resourceFilePath);
    console.log("---> app-style-hot-loader source:", source);
    const result = `${source};${reload({ type: hmrStyleType, resource: resourceFilePath })}`;
    console.log("---> app-style-hot-loader return:", result);
    return result;
};
